const books = [
  {id: 1, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 2, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 3, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 4, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 5, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 6, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 7, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 8, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 9, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},
  {id: 10, name: 'Война и мир', author: 'Л.Н. Толстой', image: 'pics/69495367.jpg', description: 'test'},]

const getBookById = (id) => {
  return books.find((book) => book.id === parseInt(id));
};

const BookList = (props) => {
  const { id } = useParams();
  const book = getBookById(id);
  if (!(id)){
    return ( <div className="book-list">
    {books.map(book => (
      <BookCard link={`/books/${book.id}`} name={book.name}
      author={book.author} image={book.image} /> ))}
      </div> );
    };
  if (!book) {
    return <div>Book not found</div>;
  }
  const { name, author, image, description } = book;
  return (
    <BookPage name={name} author={author} image={image} description={description}/>
  );
};
export default BookList;
